/**
 * 注册命名空间
 * @param {Object} name 命名空间名称
 * @param {Object} prop 扩展的属性
 */
function ns(name,prop){
    var args = arguments;
    js = window.js || {};
    js[args[0]] = js[args[0]] || {};
    if(!args[1])return;
    //这里还要处理其它库的问题
    KISSY.mix(js[args[0]],prop());
};

