#!/usr/bin/env node
exports = module.exports = function () {
  var guid = 1000000;
  return function (prefix, len) {
    return fixlen(prefix, (--guid).toString(16).toUpperCase(), len | 0 || 15);
  }

  function fixlen (prefix, s, l) {
    var _ = '_';
    var str = prefix + _ + s;
    var m = l - str.length;
    if (m <= 0) {
      return str;
    }
    return prefix + _ + new Array(m + 1).join(0) + s;
  }
};