KISSY.add('kcharts',function(S, BarChart, ScatterChart) {

  var KCharts = {
    barchart: function(cfg) {
      var config = S.mix({
        yLabels: {
          css: {
            "marginLeft": "-4px",
            "font-family": "Microsoft Yahei",
            "font-size": "10px"
          },
          template: "{{data}}"
        },
        yAxis: {
          num: 7
        },
        legend: {
          isShow: true,
          y: -10
        },
        tip: {
          offset: {
            x: 10,
            y: 10
          },
          template: function(e) {
            return e.y;
          }
        }
      }, cfg);
      return new BarChart(config);
    },
    scatterchart: function(cfg) {
      var config = S.mix({
        xGrids: {
          isShow: false
        },
        yAxis: {
          isShow: false
        },
        points: {
          attr: {
            stroke: "#000",
            "stroke-width": "1px"
          }
        },
        xAxis: {
          num: 10
        },
        yLabels: {
          css: {
            "marginLeft": "-4px",
            "font-family": "Microsoft Yahei",
            "font-size": "10px"
          }
        },
        xLabels: {
          css: {
            "padding-top": "4px",
            "font-family": "Microsoft Yahei",
            "font-size": "10px"
          }
        },
        tip: {
          css: {
            border: 0
          },
          template: function(data) {
            return "pageX:" + data.y[0] + " px, pageY:" + data.y[1] + " px";
          },
          alignX: "right",
          alignY: "bottom",
          offset: {
            x: -10,
            y: -10
          },
          boundryDetect: true
        },
        legend: {
          isShow: true
        }
      }, cfg)

      return new ScatterChart(config);

    }
  };

  return KCharts;


}, {
  requires: ["gallery/kcharts/1.3/barchart/", "gallery/kcharts/1.3/scatterchart/"]
})